INSERT INTO `ts_ask` (`askid`, `userid`, `title`, `summary`, `count_view`, `count_comment`, `isopen`, `isaudit`, `status`, `addtime`, `uptime`) VALUES
(44, 59, '孩子不喜欢听故事怎么办？', '孩子不喜欢听故事怎么办？', 0, 1, 1, 1, 1, 1395061463, 1395061463),
(45, 38, '请问,有故事的汉字,和中国童谣这两套书怎么样?请指点一下啊', '请问,有故事的汉字,和中国童谣这两套书怎么样?请指点一下啊', 0, 0, 1, 1, 1, 1396277382, 1396277382),
(3, 1095, '绘本文字那么少，会不会信息量太少？', ' 我看每一本绘本的文字都特别少，几十页就没了。孩子看完是不是得不到什么东西呢？ ', 0, 1, 1, 1, 1, 1394238994, 1394246973),
(4, 1112, '为什么很多绘本都是精装？', '我看很多绘本都是精装，价格很高，为什么要这样设计呢？', 0, 1, 1, 1, 1, 1394239076, 1394246948),
(5, 1109, '为什么很多绘本没有拼音？', '绘本不是给小孩子看的书吗？为什么没有拼音？那孩子怎么看呢？', 0, 1, 1, 1, 1, 1394239181, 1394246924),
(6, 1108, '为什么绘本这么贵？', '为什么一本小小的绘本要20、30元呢？到底贵在哪里呢？', 0, 1, 1, 1, 1, 1394239266, 1394246877),
(7, 1106, '孩子总爱看电视不爱看书怎么办？', '孩子最近特别喜欢看电视，看动画片，让看书，怎么也不看，这可怎么办啊？', 0, 1, 1, 1, 1, 1394239420, 1394246853),
(8, 1107, '怎样鼓励爸爸加入到亲子阅读的行列中来？', '我们家爸爸不怎么喜欢陪孩子，我要怎样说服他一起来跟孩子阅读呢？', 0, 1, 1, 1, 1, 1394239623, 1394246828),
(9, 1105, '孩子喜欢反复阅读一个故事正常吗？', '我家宝宝总是喜欢逮住一本书不听地让我读给他听，这样是不是不好啊？', 0, 1, 1, 1, 1, 1394239728, 1394246806),
(10, 1104, '孩子不认真听故事怎么办？', '我在给孩子读绘本的时候，她好像总是坐不住，不能专心听，有什么好办法吗？', 0, 1, 1, 1, 1, 1394239860, 1394246786),
(11, 1103, '孩子是不是不愿意听我讲故事？', '我不是很会讲故事，读起绘本来也就是按照书上的一个字一个字念，是不是孩子不喜欢我读的呢？', 0, 1, 1, 1, 1, 1394239973, 1394246752),
(12, 1102, '孩子不喜欢我给她选的书怎么办？', '我给孩子挑的书，他都不怎么想看，这怎么办呢？', 0, 1, 1, 1, 1, 1394240049, 1394246715),
(13, 1097, '6岁的孩子要看奥特曼之类的漫画书，应该给他买吗？', '如题', 0, 1, 1, 1, 1, 1394240097, 1394246687),
(14, 1096, '我是应该照着书读，还是继续随意地读？', '孩子小的时候，我不按书念，他也发现不了，现在他识一些字了，我是应该照着书读，还是继续随意地读？', 0, 1, 1, 1, 1, 1394240177, 1394246659),
(15, 1113, '孩子爸爸不看书，对孩子的阅读好吗？', '孩子的爸爸不喜欢看书，也不管孩子，这对孩子的阅读有多大影响？', 0, 1, 1, 1, 1, 1394240342, 1394246611),
(16, 1094, '怎样为孩子布置一个良好的阅读环境？', ' 在家里怎么布置，才能有很好的阅读气氛呢？ ', 0, 1, 1, 1, 1, 1394329098, 1394329163),
(17, 1094, '为什么说亲子阅读是不可替代的？', ' 亲子阅读为什么那么重要呢？ ', 0, 1, 1, 1, 1, 1394329142, 1394329142),
(18, 996, '孩子都会自己看书了，我还要给他讲故事吗？', '我家思雨自己已经认识字，可以自己看书了，我还要像以前那样继续给她读吗？还是就让她自己看就好了？', 0, 1, 1, 1, 1, 1394329534, 1394329534),
(19, 976, '孩子很喜欢古诗，我要不要有意识地引导他背几首诗呢？', '就是这些诗词啊国学的东西，背诵是不是很必要的事？', 0, 1, 1, 1, 1, 1394331169, 1394331169),
(20, 967, '孩子5岁，只讲故事就够了吗，不需要教孩子识字写字吗？', '不然上了小学不识字，怎么办呢？', 0, 1, 1, 1, 1, 1394331315, 1394331315),
(21, 960, '几个月的宝宝适合读什么样的书？', '想了解一下。谢谢。', 0, 1, 1, 1, 1, 1394331368, 1394331804),
(22, 944, '怎么给孩子读长篇的故事？', '孩子已经习惯了图文并茂的绘本，我怎么才能开始给他讲以文字为主的长篇呢?', 0, 1, 1, 1, 1, 1394332324, 1394332324),
(23, 1011, '孩子都快到了上学的年纪了，还让我给他读故事。我是不是该停止了?', '是不是不用再讲了呢？', 0, 1, 1, 1, 1, 1394332362, 1394332362),
(24, 1040, '如何在讲故事中引导孩子呢？', '我试图按照专家的建议，开发孩子的想象力。但是每次我提出建议，让他来猜猜接下来会发生什么，他总是不耐烦地催促我继续讲，他似乎有思维惰性。我该怎么引导呢?', 0, 1, 1, 1, 1, 1394332405, 1394332405),
(25, 1086, '怎么对待书中的阴暗面呢？', '经典童话故事中的巫婆的咒语、继母的毒苹果、动物们的冷嘲热讽，是不是会让孩子过早接触阴暗的东西?', 0, 1, 1, 1, 1, 1394332441, 1394332441),
(26, 1074, '讲故事时总是被打断，怎么办？', '讲的过程中孩子总是提问，我应该回答他吗，如果那样的话，故事就总被打断了?', 0, 1, 1, 1, 1, 1394332481, 1394332481),
(27, 1072, '原文照读还是自由发挥呢？', '即使是童书中也有很多书面语，我应该忠实于原著，让孩子在聆听中学会这些语言，还是应该翻译成大白话?', 0, 1, 1, 1, 1, 1394332520, 1394332520),
(28, 1063, '读故事的过程被打断怎么办？', '给孩子读绘本的时候，我读文字，但是他却对图感兴趣，经常因为图的内容打断我的话。我应该怎么办?', 0, 1, 1, 1, 1, 1394332571, 1394332571),
(29, 902, '1岁多的宝宝适合读什么样的书？', ' 想了解一下。 ', 0, 1, 1, 1, 1, 1394346830, 1394346830),
(30, 924, '3岁以上宝宝适合读什么样的书？', '嗯，我们家宝宝3岁多了，该给他选什么样的书呢？', 0, 1, 1, 1, 1, 1394346883, 1394346883),
(31, 923, '宝宝总是急着往后翻书，怎么办？', '孩子快1岁了，我满腔热情地给他讲书，可是他却总是着急地往后翻书。我还该不该读书呢?', 0, 1, 1, 1, 1, 1394347056, 1394347056),
(32, 918, '坐不住的孩子，怎么读书给他听？', '儿子7个月，根本坐不住。我应该强迫他听故事吗?', 0, 1, 1, 1, 1, 1394347170, 1394347170),
(33, 840, '孩子总是抢我手里的书，当作玩具去撕、去咬。我应该怎么办呢?', '讲书的时候，孩子非但不听，还总是抢我手里的书，当作玩具去撕、去咬。我应该怎么办呢?', 0, 1, 1, 1, 1, 1394347363, 1394347363),
(34, 843, '孩子听故事时不注意怎么办？', '我从孩子3个月大，就开始给他读故事。但是我感觉他根本不在意我的声音。', 0, 1, 1, 1, 1, 1394347423, 1394347423),
(35, 866, '孩子看电视时，怎么让他去阅读？', '孩子看电视时，怎么让他去阅读？', 0, 1, 1, 1, 1, 1394347617, 1394347617),
(36, 866, '该不该早点培养孩子自主阅读的能力？', '该不该早点培养孩子自主阅读的能力？', 0, 1, 1, 1, 1, 1394347646, 1394347646),
(37, 866, '为什么孩子在你阅读时不要强迫他去认字？', '为什么孩子在你阅读时不要强迫他去认字？', 0, 1, 1, 1, 1, 1394347677, 1394347677),
(38, 869, '建议孩子细读、精读吗？', ' 有兴趣的书,孩子读得特别快,一个小时就读完了一本.这样的阅读速度对阅读有帮助吗?建议孩子细读吗?    ', 0, 0, 1, 1, 1, 1394348227, 1394348227),
(39, 869, '孩子会不会看不懂书里在讲什么？', '孩子会不会看不懂书里在讲什么？', 0, 1, 1, 1, 1, 1394348259, 1394348259),
(40, 877, '4岁以上宝宝适合读什么样的书？', ' 求教。 ', 0, 1, 1, 1, 1, 1394348315, 1394348315),
(41, 891, '5岁以上的宝宝适合读什么样的书？', ' 宝宝5岁半了，该选择什么样的书给他呢？ ', 0, 1, 1, 1, 1, 1394348362, 1394348362),
(42, 895, '读书的时候教孩子认字不好吗？', '一边讲故事、一边根据书上的内容来问孩子问题，到底好不好？', 0, 1, 1, 1, 1, 1394348426, 1394348426),
(43, 902, '讲完故事之后，要问一些问题加深孩子对这本书的印象吗？', '讲完故事之后，要问一些问题加深孩子对这本书的印象吗？', 0, 1, 1, 1, 1, 1394348462, 1394348462),
(46, 16, '请教下,有没有讲海啸呀，下雨、地震呀这些自然现象，适合4岁左右小朋友看的绘本?', '请教下,有没有讲海啸呀，下雨、地震呀这些自然现象，适合4岁左右小朋友看的绘本?', 0, 0, 1, 1, 1, 1396277487, 1396277487),
(47, 1095, '发现宝宝没进步怎么办？', '我给我家宝宝坚持读了一段时间的绘本，没发现TA有什么明显的进步和改变，是我方法不对，还是其他什么问题？', 0, 0, 1, 1, 1, 1403760935, 1403760935),
(48, 1112, '孩子喜欢反复看一本书，这种情况需要制止吗？', '孩子喜欢反复看一本书，这种情况需要制止吗？', 0, 2, 1, 1, 1, 1403760968, 1403760968),
(49, 1109, '​给孩子读绘本时，发现孩子并不专注，一边听一边自己玩开了，需要阻止吗？', ' 给孩子读绘本时，发现孩子并不专注，一边听一边自己玩开了，需要阻止吗？ ', 0, 0, 1, 1, 1, 1403761030, 1403761030),
(50, 1108, '孩子对大奖、名家作品不感兴趣怎么办？', '我常给孩子准备一些名家推荐或者经典大奖绘本，但发现有些书我家孩子并不一定有兴趣。这是怎么回事？', 0, 0, 1, 1, 1, 1403761929, 1403761929),
(51, 1107, '需要每天给孩子固定时间阅读吗？', ' 需要每天给孩子固定时间阅读吗？    ', 0, 0, 1, 1, 1, 1403761979, 1403761979),
(52, 1106, '怎么对待结局伤感的绘本？', ' 像《彩虹色的花》《你看起来好像很好吃》这样的结局有些伤感的绘本，孩子看了很难走出悲伤的情绪，该如何引导？    ', 0, 0, 1, 1, 1, 1403762023, 1403762023),
(53, 1105, '要不要指读？', '读绘本时，为了让孩子多认字，是否该用手指着文字处理逐个指读？', 0, 0, 1, 1, 1, 1403762071, 1403762071),
(54, 1104, '何时可以开始自主阅读？', '孩子已经认识很多字了，是否可以不用陪着一起读绘本，可以让TA自主阅读了？', 0, 1, 1, 1, 1, 1403762142, 1403762142),
(55, 1103, '怎么样拓宽孩子的阅读范围？', ' 孩子现在的阅读量挺大的，但是都是有倾向性的，比较满足轻松愉快的类型，怎么样拓宽孩子的阅读范围？    ', 0, 1, 1, 1, 1, 1403762177, 1403762177),
(56, 1103, '天天给孩子读绘本，有时真的有些累，可否用电脑给孩子放绘本音频来代替给孩子读绘本么？', '天天给孩子读绘本，有时真的有些累，可否用电脑给孩子放绘本音频来代替给孩子读绘本么？', 0, 1, 1, 1, 1, 1403762200, 1403762200),
(57, 1099, '我家孩子9岁了，看书特别的快，这样好吗', '我家孩子9岁了，看书特别的快，这样好吗', 0, 1, 1, 1, 1, 1415009015, 1415009015);
