INSERT INTO `ts_special` (`specialid`, `userid`, `cateid`, `title`, `linkurl`, `content`, `path`, `photo`, `count_click`, `isaudit`, `addtime`, `status`) VALUES
(1, 2, 1, '阅读起步走', 'http://www.7jia8.com/Topics/2010/20100118_ydqbz123/', '国内外许多早期阅读研究表明，从婴幼儿时期开始培养阅读习惯的儿童，长大后，不但语言表达能力好，逻辑运算能力也较强。孩子出生后，父母就可以读书给他听，每天花一点点时间陪孩子共读。每读一本书，就像在孩子的心里播下一颗种子，在孩子成长的过程中慢慢萌芽，并结成智慧的硕果，成为孩子一生受用的知识财富。\r\n<br>　　为此，我们会同早教专家、儿童阅读推广者，精心挑选了适合婴幼儿阅读的优质图画书，分为语言表达、肢体协调、社会交往、亲子沟通、思维拓展及多元认知六方面，让爸爸妈妈能够由这份书单开启孩子的阅读之门，培育婴幼儿良好的阅读习惯。', '0/0', '0/0/1.jpg', 0, 1, 1393941079, 1),
(3, 2, 1, '心中充满阳光，人生便会灿烂', 'http://baby.dangdang.com/20140924_rga2', '凯迪克大奖是美国最权威的图画书奖，该奖之所以能够脱颖而出，获得一致推崇，主要在于其评选标准的周延和创新，奖项着重作品的艺术价值和特殊创意。', '0/0', '0/0/3.jpg', 0, 1, 1415003520, 1),
(4, 2, 1, '领略前所未见的超现实奇景', 'http://baby.dangdang.com/20131104_0xw4', '安东尼·布朗（Anthony Browne）于1946年生在英格兰的谢菲尔德，自小喜爱画画与讲故事。他创作的图画书以超现实风格著称。他往往用层层重叠的水彩技巧，巨细靡遗地描写人物、动物和环境，但是在构图当中，却故意将对象错置、比例颠倒、形体互换……造成出人意料的超现实效果。', '0/0', '0/0/4.jpg', 0, 1, 1415105644, 1),
(5, 2, 1, '透过幻想驯服心灵深处的野兽', 'http://baby.dangdang.com/20141011_rgey', '假设只允许我们推荐一本图画书给你看，那么就是非它莫属了。它是一本大书，是图画书的金字塔。迄今为止，还没有一本图画书能够超越它。可以这么说，它是一本永远也读不完的图画书。它就是莫里斯·桑达克的《野兽国》。', '0/0', '0/0/5.jpg', 0, 1, 1415282977, 1),
(6, 2, 1, '不可轻视孩子的力量', 'http://baby.dangdang.com/20130704_vkj3', '2013年凯特.格林纳威大奖！英国图画书最高荣誉，美国图书馆协会强力推荐，堪称一绝的插画，画家姬炤华、儿童阅读推广人深圳小刀、书虫点爸诚意推荐！', '0/0', '0/0/6.jpg', 0, 0, 1415668108, 1),
(7, 2, 1, '不可轻视孩子的力量', 'http://baby.dangdang.com/20130704_vkj3', '2013年凯特.格林纳威大奖！英国图画书最高荣誉，美国图书馆协会强力推荐，堪称一绝的插画，画家姬炤华、儿童阅读推广人深圳小刀、书虫点爸诚意推荐！', '0/0', '0/0/7.jpg', 0, 1, 1415668200, 1),
